import math

def calculate_probability(P, time):
    total_minutes = 1440
    probability = 0

    # Calculate the probability of rain for each minute
    for minute in range(total_minutes):
        if minute >= time:
            probability += P
        else:
            probability += 0

    # Return the probability rounded to 4 decimal places
    return round(probability / total_minutes, 4)

